import { PluginWithOptions } from 'markdown-it';

interface SnippetEnv extends Record<string, any> {
    /** included snippet files */
    snippetFiles?: string[];
}

interface MarkdownItSnippetOptions {
    /**
     * Get current filePath
     *
     * 获得当前文件路径
     *
     * @default (path) => path
     */
    currentPath: (env: SnippetEnv) => string;
    /**
     * handle include filePath
     *
     * 处理 include 文件路径
     *
     * @default (path) => path
     */
    resolvePath?: (path: string, cwd: string | null) => string;
}

declare const snippet: PluginWithOptions<MarkdownItSnippetOptions>;

export { snippet };
export type { MarkdownItSnippetOptions, SnippetEnv };
